/**
 * The random class
 *
 */
Imperion.Util.PseudoRandom = new Class.Singleton({
	/**
	 * default seed
	 * 
	 * @var int
	 */
    seed: 12345,
    
    /**
     * prepare the random class
     * 
     * @return void
     */
    initialize: function(newSeed){
        if (newSeed) {
            this.seed = newSeed;
        }
        this.randomInt32();
        this.randomInt32();
    },
    
    /**
     * set new seed
     * 
     * @param int newSeed
     * 
     * @return void
     */
    setNewSeed: function(newSeed){
        if (newSeed) {
            this.seed = newSeed;
        }
        this.randomInt32();
        this.randomInt32();
        
        return this;
    },
    
    /**
     * do somethin
     * 
     * @return int
     */
    randomInt32: function(){
        lo = 16807 * (this.seed % 0xFFFF);
        hi = 16807 * (this.seed >> 16);
        
        lo += (hi % 0x7FFF) << 16;
        lo += hi >> 15;
        
        if (lo > 0x7FFFFFFF) {
            lo -= 0x7FFFFFFF;
        }
        
        this.seed = lo;
        return this.seed;
    },
    
    /**
     * randomize something
     * 
     * @return int
     */
    randomReal32: function(){
        var randValue = this.randomInt32() / 0x7FFFFFFF;
        return Math.round(randValue * 100000000) / 100000000;
    },
    
    /**
     * create random value
     * 
     * @param int min
     * @param int max
     * 
     * @return int
     */
    rand: function(min, max){
        return Math.floor(min + (this.randomReal32() * Math.abs(max + 1 - min)));
    }
});
