			var responseData = response.getResponseData();
			if(responseData.error) {
				Imperion.Interface.Factory.eventBox().setEventBox({
					type : 'error',
					content : responseData.error
				}).showEventBox();
			} else {
				this.dialog.contexts.mailboxoutgoing.showContext();
			}
